    // Check if the snake has collided with the wall
    if (snakeX < 0 || snakeX + snakeSize > WIDTH) {
        return 1;
    }

    // Check if the snake has collided with the food
    if (snakeX == foodX && snakeX + snakeSize > foodX && snakeX < foodX + foodSize) {
