#include <string>
#include <regex>

/**
 * Converts markdown text to HTML.
 *
 * @param markdownText The markdown text to convert.
 * @return The HTML text.
 */
std::string markdownToHtml(const std::string& markdownText) {
    // Convert headings
    std::regex headingRegex("#(.*?)");
    std::string htmlText = std::regex_replace(markdownText, headingRegex, "<h$1>");
    htmlText = std::regex_replace(htmlText, headingRegex, "<h1>$1</h1>");

    // Convert bold text
    std::regex boldRegex("\\*\\*(.*?)\\*\\*");
    htmlText = std::regex_replace(htmlText, boldRegex, "<strong>$1</strong>");

    // Convert italic text
    std::regex italicRegex("\\*(.*?)\\*");
    htmlText = std::regex_replace(htmlText, italicRegex, "<em>$1</em>");

    // Convert links
    std::regex linkRegex("\\[(.*?)\\]\\((.*?)\\)");
    htmlText = std::regex_replace(htmlText, linkRegex, "<a href=\"$2\">$1</a>");

    // Convert images
    std::regex imageRegex("!\\[(.*?)\\]\\((.*?)\\)");
    htmlText = std::regex_replace(htmlText, imageRegex, "<img src=\"$2\" alt=\"$1\">");

    return htmlText;
}
```

